package ControlStatements;

public class SwitchStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int choice=10;
		
		switch(choice)
		{
		case 1:
			System.out.println(" I AM CHOICE 1");
         break;
         
		case 2:
			System.out.println(" I AM CHOICE 2");
         break;
		case 3:
			System.out.println(" I AM CHOICE 3");
         break;
		case 4:
			System.out.println(" I AM CHOICE 4");
         break;
		case 5:
			System.out.println(" I AM CHOICE 5");
         break;
         
		
		default:
			System.out.println("I am default statement");	
		}
		
	}

}
